mapper.select:
select count(1)
from goods
where goods_name like #{keyWord,jdbcType=VARCHAR}

mapper.update:
update goods
set goods_status = 0
where goods_id = #{goodsId,jdbcType=VARCHAR}

update goods
set goods_status = 1
where goods_id = #{goodsId,jdbcType=VARCHAR}

update goods
set goods_stock = goods_stock - #{count,jdbcType=INTEGER}
where goods_id = #{goodsId,jdbcType=VARCHAR}
  and goods_stock >= #{count,jdbcType=INTEGER};
