	shipping_name          = #{shippingName,jdbcType=VARCHAR},
	shipping_mobile_number = #{shippingMobileNumber,jdbcType=INTEGER},
	shipping_address       = #{shippingAddress,jdbcType=VARCHAR}
where shipping_id = #{shippingId,jdbcType=VARCHAR}
